INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (485, 'Java Software Engineer Back End at Textkernel (Amsterdam, Netherlands)', '&lt;p&gt;We are looking for talented, smart, and passionate software engineers for the web-application and web-service development team. At Textkernel you are expected to have a large responsibility for your work and a direct impact on the products. The role is diverse and challenging, as it involves both solving difficult software engineering tasks and ensuring the products satisfy the customer&amp;rsquo;s needs.&lt;br&gt;You will contribute major new functionality to Sourcebox, Textkernel&amp;rsquo;s flagship CV parsing, semantic search and document workflow system in a small highly skilled team using scrum and other agile methods. We use both open source as well as internally developed proprietary technologies.&lt;/p&gt;', 'Amsterdam, Netherlands', 'http://careers.stackoverflow.com/jobs/99893/java-software-engineer-back-end-textkernel', 'Thu, 08 Oct 2015 14:02:02 Z', '2015-10-08T14:02:02Z', 485);
