INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (499, 'Softwareentwickler(in) mit Schwerpunkt C++ &amp; C# at Inreal Technologies GmbH (Karlsruhe, Deutschland)', '&lt;p&gt;Inreal&amp;reg; Technologies GmbH bietet virtuelle Geb&amp;auml;uderundg&amp;auml;nge zur Vermarktung und Planung von Immobilien. Diese Rundg&amp;auml;nge werden in der Unreal Engine 4 von unseren Artists umgesetzt. Unser Softwareteam erg&amp;auml;nzt diese Rundg&amp;auml;nge z.B. mit einem User-Interface, Materialswitches und einem f&amp;uuml;r Kunden zug&amp;auml;nglichen Editor.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&amp;nbsp;&lt;strong&gt;Deine Aufgaben&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Implementierung und Entwurf von Softwarekomponenten&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Entwicklung von Plugins und Flowgraphs f&amp;uuml;r die Unreal Engine 4&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Kundenkontakt zur Konzeption und Abnahme von Entwicklungsprojekten&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Karlsruhe, Deutschland', 'http://careers.stackoverflow.com/jobs/99631/softwareentwickler-in-mit-schwerpunkt-c-plus-plus-c-sharp-inreal-technologies-gmbh', 'Thu, 08 Oct 2015 14:02:02 Z', '2015-10-08T14:02:02Z', 499);
