INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (505, 'Software Engineer (clearance required) at Integrity Applications Incorporated (IAI) (Lexington Park, MD)', '&lt;p&gt;IAI Software Engineers develop breakthrough products and innovative software applications for our government customers. We are looking for engineers who are versatile, have demonstrated an outstanding level of academic achievement, enjoy working in a fast based environment, like to consistently tackle new problems and exceed customers&amp;rsquo; expectations.&amp;nbsp; We have multiple openings for junior to mid-level engineers (salary commiserate with experience).&amp;nbsp;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Responsibilities:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Work with team lead to define software requirements&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Design, develop, system software for our customers&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Explore new areas or program in new languages to deliver a complete product&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Support on-site meetings and deliveries to customers&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Lexington Park, MD', 'http://careers.stackoverflow.com/jobs/99532/software-engineer-clearance-required-integrity-applications', 'Thu, 08 Oct 2015 14:02:02 Z', '2015-10-08T14:02:02Z', 487);
