INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (552, 'Lead Full-stack Software Engineer at Fittery (Atlanta, GA)', '&lt;p&gt;You&amp;rsquo;ll be working on a brand new digital consumer marketplace, designed to radically transform the way consumers shop for apparel. We are in beta right now and and we already have over 30 brand customers, including J. Crew, Express, Saks, Macy&amp;rsquo;s, Bonobos and Jos. A Bank. As one of the earliest employees, you&amp;rsquo;ll be given both salary and an ownership stake in the company as we set our sights on rapid growth and expansion. You&amp;rsquo;ll be:&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Building the product and technology roadmap in partnership with the company&amp;rsquo;s founders.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Evaluating and identifying appropriate technology architecture and platforms (including web application frameworks and the deployment stack).&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Communicating the company&amp;rsquo;s technology strategy to the team, partners, customers, stakeholders, and investors.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Evolving all of the applications, both consumer-facing and back-end.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Selecting, deploying, and monitoring operational tools to ensure all applications are healthy, with a view toward high performance and speed.&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Atlanta, GA', 'http://careers.stackoverflow.com/jobs/98322/lead-full-stack-software-engineer-fittery', 'Thu, 08 Oct 2015 13:02:02 Z', '2015-10-08T13:02:02Z', 552);
