INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (556, 'Senior PHP Developer at nestpick Global Services GmbH (Berlin, Germany)', '&lt;p&gt;&lt;strong&gt;WHO ARE WE&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;We revolutionize the rental market - worldwide. Estate agents hate us, landlords and tenants love us.&lt;/p&gt;&lt;br /&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;WHO ARE YOU&lt;br&gt;&lt;/strong&gt;&lt;br&gt;We are hiring likeminded people who don''t always share the same opinion but as a team reach the best possible outcome. What else? Tick the list:&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;You are NOT an asshole&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;You excuse the language above&lt;/li&gt;&lt;br /&gt;&lt;li&gt;You are fluent in English (writing and speaking)&lt;/li&gt;&lt;br /&gt;&lt;li&gt;You have successfully finished your degree&amp;nbsp;in the field of computer sciences or equivalent professional experience&lt;/li&gt;&lt;br /&gt;&lt;li&gt;You have 6+ years&amp;nbsp;of experience in development with PHP OOP&lt;/li&gt;&lt;br /&gt;&lt;li&gt;You have experience with most of the technologies described below in TOPICS, with an exceptional in-depth skill-set on the backend topics&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Your working style is clean, fast and reliable&lt;/li&gt;&lt;br /&gt;&lt;li&gt;You want to live in Berlin&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;&lt;br&gt;TOPICS&lt;br&gt;&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Focus: PHP 5 OOP, MVC, Design Patterns, Symfony&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Focus: PHPUnit, PHP CodeSniffer, phpDoc&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Focus: REST, JSON, SOAP&lt;/li&gt;&lt;br /&gt;&lt;li&gt;HTML5, CSS, Sass, jQuery, ExtJS, Angular, Ajax, Grunt&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Debian, Apache, Nginx, MySQL, Memcache, Solr, Redis&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Github, Kanban, Cucumber, Jenkins, Scrutiziner&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;&lt;br&gt;&lt;br&gt;WHAT YOU WILL DO&lt;/strong&gt;&lt;strong&gt;&lt;br&gt;&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Design and develop complex web applications and new features for our classifieds platform&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Optimize our platform for scalability and ensure code quality&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Enforce best practices by communicating and sharing them to team members&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Find ways to do our work more efficiently and reliably&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Support junior developers with questions and code-reviews&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;&lt;br&gt;WHAT WE OFFER&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Your opportunity to build the real estate platform for the new generation and&amp;nbsp;scale applications internationally&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Personal and professional development in a multi-cultural and multi-professional environment&lt;/li&gt;&lt;br /&gt;&lt;li&gt;A team willing to go the extra mile for you&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Your own device: Win, Mac or Linux&amp;nbsp;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Responsibility and autonomy in agile teams and fast communication&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Rewarding and competitive pay&lt;/li&gt;&lt;br /&gt;&lt;li&gt;and finally: Burritos, Pizza, Drinks and movie-nights :-)&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;If this sounds like you, get in touch with me at&amp;nbsp;eike@nestpick.de&amp;nbsp;and drop me a couple of lines to why we should hire you as Senior PHP Developer.&lt;/strong&gt;&lt;/p&gt;', 'Berlin, Germany', 'http://careers.stackoverflow.com/jobs/87775/senior-php-developer-nestpick-global-services-gmbh', 'Thu, 08 Oct 2015 13:02:02 Z', '2015-10-08T13:02:02Z', 556);
