INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (559, 'Oracle DBA / Data Warehouse Developer (m/w) at adviqo AG (Berlin, Deutschland)', '&lt;p&gt;Ein internationales Team aus Spezialisten verschiedenster Fachrichtungen freut sich auf Dich als neuer&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Oracle DBA / Data Warehouse Developer (m/w)&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;br /&gt;&lt;br /&gt;&lt;p&gt;Deine Aufgaben:&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Du sorgst f&amp;uuml;r maximale Ausfallsicherheit, optimale Performance und st&amp;auml;ndige Verf&amp;uuml;g&amp;shy;barkeit bei unseren Datenbanken (aktuell Oracle).&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Du verantwortest die Konzeption, Umsetzung und Integration relationaler Datenbank&amp;shy;systeme, die korrekte Einbindung in das Programmierumfeld sowie die Administration hochverf&amp;uuml;gbarer Datenbankclustersysteme.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Du implementierst und optimierst ETL-Strecken mit einem ETL-Tool.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Du bist verantwortlich f&amp;uuml;r die Sicherstellung und Optimierung der Datenqualit&amp;auml;t im DWH.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Du erstellst und pflegst die Dokumentationen.&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Berlin, Deutschland', 'http://careers.stackoverflow.com/jobs/99879/oracle-dba-data-warehouse-developer-m-w-adviqo-ag', 'Thu, 08 Oct 2015 13:02:02 Z', '2015-10-08T13:02:02Z', 559);
