INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (571, 'Infrastructure Engineer at Unibet (Stockholm, Sweden)', '&lt;p&gt;7+ Data Centers, on 2 continents, with 250+ Systems and 600.000+ Active users in a 24/7 online business.&lt;br&gt;Join our team challenge, building a scalable IT infrastructure for 1000+ changes a year with a rapidly growing market.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;br&gt;We are looking for a Senior infrastructure Engineer to join our Infrastructure team in Stockholm that is part of a large passionate department covering Infrastructure, Network, Application and Database to deliver the best technical platform for Unibet. Technical Development is responsible for the non-functional requirements within Unibet. The role will work with areas such as architecture, design, project, implementation and third-line support to Operations.&lt;/p&gt;', 'Stockholm, Sweden', 'http://careers.stackoverflow.com/jobs/99877/infrastructure-engineer-unibet', 'Thu, 08 Oct 2015 13:02:02 Z', '2015-10-08T13:02:02Z', 571);
