INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (585, 'Spring, RESTful, SOA Web Services Developer at Fidelity (Durham, NC)', '&lt;p&gt;As Fidelity continues to expand its suite of product offerings and platforms in our managed accounts space we are hiring more web services developers to apply technical expertise to product delivery.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Fidelity&amp;rsquo;s &lt;strong&gt;Strategic Investment Products and Data&lt;/strong&gt; (SIPD) group is a shared services provider of technology platforms and product solutions designed to drive successful business outcomes across many of the firm&amp;rsquo;s most critical areas of growth.&amp;nbsp; A fast-growing team of more than 900 associates located throughout the United States, China, India and Ireland, ours is a culture that is committed to innovation, supportive of continuous learning, focused on building teams and relationships and one that places a high value on maintaining an environment of trust and transparency. &lt;br&gt;&lt;strong&gt;Primary Responsibilities&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Day to day responsibilities require significant lean and agile software development experience running team meetings, scrum iterations, AGILE projects in a fast paced financial analytics environment.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Be able to focus strong technical experience and project management skills to ensure software product development is delivered on time and of the highest quality.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Foster teamwork and a spirit of collaboration among team members and business partners while conveying a sense of urgency and responsiveness to meet business needs.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Contribute to or write concise and clear technical specifications based on analysis of complex business requirements&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Driving product delivery through building innovative web services utilizing Continuous Integration (CI) and Continuous Deployment (CD), supporting your solution from Development through to Production.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Analyzing, describing, and modeling new and existing business and technology processes with a primary focus on how these processes manipulate and use the data&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Responsible for coordinating multiple development work streams through integration and ensures architecture is scalable and extensible&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Capable of completing OOD and development of the most complex components of the application&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Define, maintain and support Java development environment and best practices such as Maven, Sonar, and other Open Source tools&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Plays a key role in resolving technical roadblocks involving multiple technologies&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Continually improve quality of development team deliverables&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Ensures code reviews, unit testing and integration testing is completed efficiently and effectively&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Help coordinate the delivery of technical components across internal/external technical teams.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Provide technical leadership to more junior team members of the team&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Contribute to process-improvement initiatives in an effort to maximize velocity for the product team&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Durham, NC', 'http://careers.stackoverflow.com/jobs/97278/spring-restful-soa-web-services-developer-fidelity', 'Thu, 08 Oct 2015 13:02:02 Z', '2015-10-08T13:02:02Z', 262);
