INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (590, 'Veteran Ruby on Rails team seeks reinforcements (REMOTE) at E-accent BV (Hilversum, Netherlands) (allows remote)', '&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Wide range of large&amp;nbsp;Ruby on Rails projects in commerce, legislation and healthcare&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Work from the comfort of your own home&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Be part of&amp;nbsp;a capable, supportive international team&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Actual 40-hour work week&lt;/li&gt;&lt;br /&gt;&lt;li&gt;No pair programming, no managers, nothing to disrupt your efficiency&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Three 30-minute team meetings per week&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;p&gt;As a developer at E-accent, you&amp;rsquo;ll help build complex web applications that solve real commercial and practical problems for our clients.&amp;nbsp;In many cases, the code you write will be&amp;nbsp;absolutely critical to the successful operation of the client&amp;rsquo;s business.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;All our code is peer-reviewed to ensure quality, and we take great pride in doing the best work we possibly can.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;This is a full-time, remote role. We don&amp;rsquo;t believe long hours are productive &amp;ndash; we work eight hours a day, five days a week, unless there&amp;rsquo;s a genuine emergency (which is rare). The atmosphere in our company&amp;nbsp;is friendly, nurturing and supportive, with no room for big egos. Your first few months on the job you''ll have a "sponsor" &amp;ndash; another team member who can help you learn the ropes.&lt;/p&gt;', 'Hilversum, Netherlands', 'http://careers.stackoverflow.com/jobs/99874/veteran-ruby-on-rails-team-seeks-reinforcements-e-accent-bv', 'Thu, 08 Oct 2015 13:02:02 Z', '2015-10-08T13:02:02Z', 590);
