INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (592, 'Senior Ops Automation Engineer (m/f) at Delivery Hero (Berlin, Germany)', '&lt;p&gt;&lt;strong&gt;About the Job:&lt;br&gt;&lt;br&gt;&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;We are building our platform stack on a modern set of technologies, and design it from the ground up for horizontal scaling. Big part of the scalability relies on the ability to automate all parts of the system management - provisioning, configuration, monitoring, (auto-)scaling.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;&lt;br&gt;Your mission:&lt;br&gt;&lt;br&gt;&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Your mission will be to help us build a system with very high level level of uptime, allowing us to guarantee uninterrupted service to the businesses that rely on our products.&lt;/p&gt;', 'Berlin, Germany', 'http://careers.stackoverflow.com/jobs/97275/senior-ops-automation-engineer-m-f-delivery-hero', 'Thu, 08 Oct 2015 12:02:02 Z', '2015-10-08T12:02:02Z', 592);
