INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (602, 'JavaScript Engineer - Mid/Snr (m/f) at Delivery Hero (Berlin, Germany)', '&lt;p&gt;&lt;strong&gt;About the job:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;br /&gt;&lt;p&gt;Delivery Hero is building the next generation global online food ordering platform. Our awesome international team already operates in over 34+ countries worldwide. We feature an environment with feedback exchange throughout the company from people who are genuine, ambitious and fun. We are looking for passionate and smart people to join us on our mission. Our ideal candidate will be enthusiastic, innovative, and good at &amp;ldquo;getting things done&amp;ldquo;. Play an active role in our future. An exciting job opportunity in the heart of Berlin is waiting for you.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&amp;nbsp;&lt;strong&gt;Your mission:&lt;/strong&gt;&amp;nbsp;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;As part of our expansion, we are looking for talented frontend engineers with a passion for developing cutting-edge API driven JavaScript applications.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;We are a team of engineers, with a deep respect for the quality of experience that can be achieved with modern browsers, and love the challenge of getting into the details with this complex and rewarding technology stack.&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Berlin, Germany', 'http://careers.stackoverflow.com/jobs/97273/javascript-engineer-mid-snr-m-f-delivery-hero', 'Thu, 08 Oct 2015 12:02:02 Z', '2015-10-08T12:02:02Z', 592);
