INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (607, 'Webbutvecklare till snabbvxande produktbolag at Kundo (Stockholm, Sverige)', '&lt;p&gt;&lt;strong&gt;Vi s&amp;ouml;ker en entusiastisk utvecklare&lt;/strong&gt; som brinner f&amp;ouml;r webben och vill hj&amp;auml;lpa oss att g&amp;ouml;ra &lt;a href="http://kundo.se" rel="nofollow" rel="nofollow" rel="nofollow"&gt;Kundo&lt;/a&gt; &amp;auml;nnu b&amp;auml;ttre. I rollen f&amp;aring;r du stor frihet och en rolig utmaning i att utveckla tj&amp;auml;nster f&amp;ouml;r hundratusentals anv&amp;auml;ndare. Och bullar. Du f&amp;aring;r&amp;nbsp;bullar.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Du &amp;auml;r en duktig utvecklare med 2 &amp;aring;rs arbetserfarenhet eller mer. Du har jobbat med Python/Django tidigare, eller har erfarenhet av andra moderna webbramverk som Ruby on Rails, Node o.s.v. Du &amp;auml;r van att jobba i st&amp;ouml;rre webbprojekt med flera kollegor, och gillar b&amp;aring;de nyutveckling och f&amp;ouml;rvaltning. Eftersom vi &amp;auml;r ett litet f&amp;ouml;retag beh&amp;ouml;ver vi en kollega med bred kompetens, s&amp;aring; om du dessutom &amp;auml;r bra gr&amp;auml;nssnittsutveckling eller devops &amp;auml;r det meriterande.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Du &amp;auml;r social, sj&amp;auml;lvst&amp;auml;ndig och gillar att ha kundkontakt. Vi bygger tj&amp;auml;nster f&amp;ouml;r kundservice och vill vara extra duktiga p&amp;aring; service mot v&amp;aring;ra egna kunder. Vi hj&amp;auml;lps alla &amp;aring;t att besvara fr&amp;aring;gor fr&amp;aring;n kunder som h&amp;ouml;r av sig. D&amp;auml;rf&amp;ouml;r beh&amp;ouml;ver du ocks&amp;aring; vara serviceminded och van att kommunicera b&amp;aring;de med kunder och kollegor.&lt;/p&gt;', 'Stockholm, Sverige', 'http://careers.stackoverflow.com/jobs/87905/webbutvecklare-till-snabbv%C3%A4xande-produktbolag-kundo', 'Thu, 08 Oct 2015 12:02:02 Z', '2015-10-08T12:02:02Z', 607);
