INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (612, 'C++ Developer (Remote Administrator) Prague, Czech Republic at ESET (Prague, Czech Republic)', '&lt;p&gt;&lt;strong&gt;What does working as C++ Developer entail?&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;C++ developers working on ESET Remote Administrator are involved in developing server-side software, which enables administrators to manage and configure ESET security products deployed on individual devices.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;To give you a&amp;nbsp;little taste, a&amp;nbsp;typical task for a&amp;nbsp;C++ developer working in our team can be as follows: &lt;em&gt;&amp;ldquo;Please propose and implement a&amp;nbsp;new network protocol, data replication between servers and new functionality in the&amp;nbsp;user web interface. Please do it with a&amp;nbsp;high degree of effectiveness because our server system has to be able to handle tens to hundreds of thousands of endpoints.&amp;rdquo;&lt;/em&gt;&lt;/p&gt;', 'Prague, Czech Republic', 'http://careers.stackoverflow.com/jobs/85180/c-plus-plus-developer-remote-administrator-prague-eset', 'Thu, 08 Oct 2015 12:02:02 Z', '2015-10-08T12:02:02Z', 612);
