INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (622, 'Frontend Entwickler (m/w) at TWT Interactive GmbH (Dsseldorf, Deutschland)', '&lt;p&gt;&lt;strong&gt;Du willst f&amp;uuml;r eine dynamisch wachsende, erfolgreiche Internet- und Online-Marketing-Agentur arbeiten? Du bist engagiert, motiviert und hast Spa&amp;szlig; an digitalen Medien?&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Als Frontend Entwickler (m/w) arbeitest Du aktiv mit bei der Konzeption, Entwicklung und Umsetzung von interaktiven, modernen Websites und mobilen Applikationen f&amp;uuml;r Desktop, Smartphones und Tablets. Frameworks und Tools wie jQuery, jQuery Mobile, AngularJS, Grunt oder Bootstrap geh&amp;ouml;ren zu deinem Entwicklungs-Repertoire. Du besch&amp;auml;ftigst Dich auch mit anderen Frameworks und bist neuen Technologien gegen&amp;uuml;ber aufgeschlossen.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Deine Aufgaben:&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Eigenverantwortliche Erstellung von interaktiven Websites auf Basis von HTML, CSS und JavaScript in enger Zusammenarbeit mit Designern und Backend-Entwicklern&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Implementierung von Templates in verschiedene Systeme (Oxid, TYPO3, Liferay, Drupal etc.)&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Umsetzung von Screendesigns in HTML und CSS&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Erfahrungsaustausch und Know-How-Sharing in einem gro&amp;szlig;en Entwicklerteam&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Dsseldorf, Deutschland', 'http://careers.stackoverflow.com/jobs/99870/frontend-entwickler-m-w-twt-interactive-gmbh', 'Thu, 08 Oct 2015 11:02:02 Z', '2015-10-08T11:02:02Z', 622);
