INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (624, 'Junior Developer at tcpl (London, UK)', '&lt;p&gt;&lt;strong&gt;The Role&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;tcpl are looking for a smart, self-motivated Junior&amp;nbsp;Software Developer to join our exceptional and experienced development team.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;About You&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;You are a hands on developer who likes to craft great solutions&lt;/li&gt;&lt;br /&gt;&lt;li&gt;You will have some exposure&amp;nbsp;in developing software&lt;/li&gt;&lt;br /&gt;&lt;li&gt;You are keen to learn new skills and apply them to real world enterprise level solutions&lt;/li&gt;&lt;br /&gt;&lt;li&gt;You will be joining an extremely talented team who are good at what they do and enjoy collaborating on a daily basis&lt;/li&gt;&lt;br /&gt;&lt;li&gt;You want to work&amp;nbsp;with some of the biggest names in retail on a range of fantastic projects&lt;/li&gt;&lt;br /&gt;&lt;li&gt;You will have the opportunity to work on any areas that makes up a modern e-commerce solution - front-end website through to the applications that manage it&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Join Us&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Our success is down to our hard-working and talented people who are committed to our customers and the niche industries in which we operate. Our people are fundamental to every action we take, every piece of knowledge we impart, and every technical solution we put out there. It''s their expertise and experience that powers our success, and in return, we want to create an enjoyable place to work that empowers them with control on how best to contribute.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;You will have the opportunity to work with some of the &lt;a href="https://www.tcpl-e.com/casestudies" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow"&gt;biggest and best companies in the retail sector&lt;/a&gt;.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;The opportunity to learn from and work alongside some of the &lt;a href="https://www.tcpl-e.com/about" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow"&gt;most experienced e-commerce professionals&lt;/a&gt; working in the online retail sector.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;We are delivery focused, not clock-watchers, and we want you to drive where you go in your technical career with us.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Technology We Use&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Most of our stack is C#/.Net but we''re using and investigating many other languages and technologies. We''d be interested in candidates from any background as long as you have a keen understanding of Object Oriented languages. Here''s a (not exclusive) list of technologies we currently use:&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;C#, JavaScript, HTML, CSS, SASS, T-SQL, Powershell&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Microsoft.Net 4.6, ASP.Net MVC, WCF, REST, jQuery, AngularJS, IIS 7.5+, nginx&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Visual Studio 2015, ReSharper, dotTrace, dotMemory, Sublime&lt;/li&gt;&lt;br /&gt;&lt;li&gt;SQL Server Technologies, RavenDB, Solr/Lucene Search, Elasticsearch&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Castle Windsor, NUnit, RhinoMocks&lt;/li&gt;&lt;br /&gt;&lt;li&gt;GitHub, TeamCity, Octopus Deploy, Chef, New Relic&lt;/li&gt;&lt;br /&gt;&lt;li&gt;YouTrack, Trello, Harvest&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Amazon Web Services, Windows Azure, Rackspace&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;p&gt;STRICTLY NO AGENCIES&lt;/p&gt;', 'London, UK', 'http://careers.stackoverflow.com/jobs/99750/junior-developer-tcpl', 'Thu, 08 Oct 2015 11:02:02 Z', '2015-10-08T11:02:02Z', 624);
