INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (626, 'Discovery Software Engineer (m/f) at Rocket Internet SE (Berlin, Germany)', '&lt;p&gt;&lt;strong&gt;About the Role&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Rocket Internet&amp;rsquo;s discovery team is a fast-growing interdisciplinary team of engineers that design, build and maintain a real-time data platform that is at the heart of the search and recommendation experience of our e-commerce and marketplace ventures. We are looking for engineers who don&amp;rsquo;t like to be pigeon-holed into one role, but look beyond the platform&amp;rsquo;s current direction and are able to anticipate what needs to be in place in six months to support future platform development and growth. The day-to-day tasks of the infrastructure and relevance engineers on the team range from building a global infrastructure that handles millions of requests per second to development of powerful ranking algorithms based on statistical models derived from large data sets as much as intuition.&lt;/p&gt;', 'Berlin, Germany', 'http://careers.stackoverflow.com/jobs/89296/discovery-software-engineer-m-f-rocket-internet-se', 'Thu, 08 Oct 2015 11:02:02 Z', '2015-10-08T11:02:02Z', 626);
