INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (642, 'Systems Engineer - Windows OS, SQL Server, VMWare - Edinburgh at Axios Systems (Edinburgh, UK)', '&lt;p&gt;The Technical Services team is part of the Axios Service Centre based in Edinburgh and is responsible for the entire global infrastructure.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;The team develop, support and manage services for our end users and customers world-wide, across a mix of both Windows and Linux platforms. The Technical Services remit is nothing but varied and runs from the desktop right back to the fibre in the comms room. &amp;nbsp;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;&lt;strong&gt;Job purpose&lt;/strong&gt;&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Play a key role in the maintenance, implementation and development of our growing Software as a Service (SaaS) platform&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Work as part of a team supporting a mix of Linux and Windows systems as well as various database platforms.&lt;/li&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;These systems include Windows 2008 - 2012, Windows 7, VMWare ESXi, SQL Server, Cisco Networking and Linux OS (Debian).&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;li&gt;Work to provide both on-site and remote support to end users and customers across all of our internal and external customer base.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;To work with the business to assess requirements for platform upgrades/deployments and work to plan, deliver and support these environments.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;To play an active part in supporting your colleagues and working closely within this team environment&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Be prepared to step out of your comfort zone and develop your skillset &amp;ndash; we&amp;rsquo;re a close knit team with a wide remit, we actively encourage personal development and skills transfer&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Edinburgh, UK', 'http://careers.stackoverflow.com/jobs/99866/systems-engineer-windows-os-sql-server-vmware-axios-systems', 'Thu, 08 Oct 2015 10:02:02 Z', '2015-10-08T10:02:02Z', 642);
