INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (652, 'Senior C++ Developer at Sisense (Tel Aviv-Yafo, Israel)', '&lt;p&gt;We are Sisense. We disrupt the Business Intelligence market. Our product allows business users to analyze humongous amounts of data without being experts and without help from IT. How? By building on a set of unique and cutting edge technologies, wrapped up in a killer product. We are looking&amp;nbsp;for talented C/C++ engineer to be part of our core technology team.&amp;nbsp;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;As an engineer in Sisense, you will be part of the team developing the core of our Big Data Analytics Columnar Database, ElastiCube.&lt;br&gt;ElastiCube is an award winning database with an advanced query execution engine.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;You will develop and improve advanced features/technologies including:&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Big Data transformation engine (TB scale).&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Multidimensional queries (advanced analytics)&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Cache-oblivious algorithms.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Superscalar decompression.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;In-chip vectorization.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Parallel I/O.&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Tel Aviv-Yafo, Israel', 'http://careers.stackoverflow.com/jobs/87879/senior-c-plus-plus-developer-sisense', 'Thu, 08 Oct 2015 09:02:02 Z', '2015-10-08T09:02:02Z', 652);
