INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (655, 'Junior Web Developer for exciting Amsterdam start-up! at Qusus (Amsterdam, Netherlands)', '&lt;p&gt;&lt;strong&gt;Junior Web Developer for Qusus&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;We are looking for an ambitious and detail oriented Junior Web Developer to work with our CTO on the development process of our upcoming application and website. You may be a recent graduate looking for a very challenging first job or a web developer with up to 5 years&amp;rsquo; experience looking to broaden and deepen your development skillset.&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Qusus is a new platform (still in pre-beta stage) built to help smart individuals earn rewards for submitting positive suggestions or new ideas for the products and services they use every day. Read more about us on: &lt;a href="http://www.qusus.com" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow"&gt;www.qusus.com&lt;/a&gt;.&lt;/p&gt;&lt;br /&gt;&lt;br /&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;If you:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Have up to 3 years&amp;rsquo; experience in frontend and backend web development&amp;nbsp;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Have a computer science background&amp;nbsp;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Are a fast learner&amp;nbsp;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Like the thought of being a part of a highly ambitious start-up&amp;nbsp;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Are passionate about web development&amp;nbsp;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Are highly skilled in Angular JS, HTML5 (front-end), Go (back-end) and Linux.&amp;nbsp;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Are highly flexible and enjoy working in a team as well as on your own&amp;nbsp;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Are available immediately for a fulltime position&amp;nbsp;&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;p&gt;Then you might be the person we are looking for!&amp;nbsp;&lt;/p&gt;&lt;br /&gt;&lt;br /&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;We offer:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;A dedicated, ambitious and friendly team from all over the world&amp;nbsp;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Involvement in a complicated tech project that can be rolled out globally&amp;nbsp;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;A competitive salary and future opportunity for equity&amp;nbsp;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Opportunity to build your portfolio and enhance your skillset tremendously&amp;nbsp;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Opportunity to grow within our rapidly expanding company&amp;nbsp;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;A workplace in one of the most creative start-up hubs in central Amsterdam&amp;nbsp;&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Sounds good?&amp;nbsp;&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;If you think you can contribute to the success and growth of Qusus with your web development and interpersonal skills, please get back to us so that we can discuss this opportunity further.&amp;nbsp;&lt;/p&gt;', 'Amsterdam, Netherlands', 'http://careers.stackoverflow.com/jobs/99861/junior-web-developer-for-exciting-amsterdam-start-qusus', 'Thu, 08 Oct 2015 09:02:02 Z', '2015-10-08T09:02:02Z', 655);
