INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (658, '(Senior) Backend Developer at flinc (Darmstadt, Deutschland)', '&lt;p&gt;You are an experienced and enthusiastic backend developer and you are looking for a job that can change the world? You are thrilled to develop in a start-up?&amp;nbsp;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;We are flinc! We want to revolutionize transportation&amp;nbsp;by negotiating free seats in cars in real-time. We analyse data of GPS and in-car systems and help drivers and passengers to share their rides. We create a new means of transportation that is as comfortable as a taxi and as cheap as the local public transport. We are looking for experienced backend developers that help us make this vision come true!&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;We use the following technologies and concepts&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Ruby on Rails 4, Ruby 2.2, PostgreSQL 9, PostGIS, Redis&lt;/li&gt;&lt;br /&gt;&lt;li&gt;RSpec, Continuous Integration via Travis CI&lt;/li&gt;&lt;br /&gt;&lt;li&gt;jQuery, Backbone.js, Handlebars, HAML, SASS, Compass&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Git, GitHub&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;What you can expect&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;An experienced and motivated team&amp;nbsp;you can learn from&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Agile software development with scrum&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Collaboration on concepts and new technologies&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Flexible working hours and result-oriented approach&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Independent and autonomous work process, free choice of tools&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Continuing education on international conferences&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Many users that challenge us to scale our system&lt;/li&gt;&lt;br /&gt;&lt;li&gt;A really cool product with state-of-the-art technology&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Cooperation with big and well-known partners&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Darmstadt, Deutschland', 'http://careers.stackoverflow.com/jobs/94771/senior-backend-developer-flinc', 'Thu, 08 Oct 2015 09:02:02 Z', '2015-10-08T09:02:02Z', 658);
