INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (669, 'Senior Testspezialist (m/w) at Zhlke Engineering GmbH (Eschborn, Deutschland)', '&lt;p&gt;Zur Verst&amp;auml;rkung unserer Teams in Eschborn (Frankfurt/Main) und Hannover suchen wir&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Senior Testspezialisten (m/w)&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Ihre T&amp;auml;tigkeit&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Ihren fachlichen Erfahrungen entsprechend &amp;uuml;bertragen wir Ihnen bei der Umsetzung von Projekten die Aufgaben:&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Erstellen und Ausf&amp;uuml;hren von Testf&amp;auml;llen anhand von Spezifikationen auf den Teststufen Integration und System&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Entwicklungsbegleitendes Testen in enger Absprache mit den Ingenieuren&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Bewerten der Testergebnisse und Ableiten von Ma&amp;szlig;nahmen zur Qualit&amp;auml;tsverbesserung&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;p&gt;Erg&amp;auml;nzt wird Ihr T&amp;auml;tigkeitsspektrum um Machbarkeitsanalysen und Aufwandsabsch&amp;auml;tzungen. In der Projektarbeit bringen Sie Ihre Ideen f&amp;uuml;r die Sicherstellung einer hohen Qualit&amp;auml;t der Software ein. Sie unterst&amp;uuml;tzen unsere Kunden aus unterschiedlichen Branchen und testen beispielsweise Produkte f&amp;uuml;r Konsumg&amp;uuml;ter, f&amp;uuml;r die Medizintechnik oder f&amp;uuml;r die Industrie.&lt;/p&gt;', 'Eschborn, Deutschland', 'http://careers.stackoverflow.com/jobs/97266/senior-testspezialist-m-w-z%C3%BChlke-engineering-gmbh', 'Thu, 08 Oct 2015 08:02:02 Z', '2015-10-08T08:02:02Z', 669);
