INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (672, 'Embedded Software-Entwickler (m/w) at Zhlke Engineering GmbH (Eschborn, Deutschland)', '&lt;p&gt;Zur Verst&amp;auml;rkung unserer Teams in Eschborn (Frankfurt am Main), M&amp;uuml;nchen, Stuttgart, und Hannover suchen wir&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Embedded Software-Entwickler (m/w)&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Ihre T&amp;auml;tigkeit&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Ihren fachlichen Erfahrungen entsprechend &amp;uuml;bertragen wir Ihnen bei der Umsetzung von strategischen Projekten Verantwortung f&amp;uuml;r Design und Entwicklung von eingebetteten Systemen. In der Projektarbeit bringen Sie Ihre Ideen ein und bauen funktionierende L&amp;ouml;sungen im Team zusammen. Erg&amp;auml;nzt wird Ihr T&amp;auml;tigkeitsspektrum um Machbarkeitsanalysen und Aufwandsabsch&amp;auml;tzungen. Sie arbeiten mit unseren Kunden aus unterschiedlichen Branchen zusammen, dazu geh&amp;ouml;ren Energiewirtschaft, Logistik, Medizintechnik und Automatisierungstechnik.&lt;/p&gt;', 'Eschborn, Deutschland', 'http://careers.stackoverflow.com/jobs/95799/embedded-software-entwickler-m-w-z%C3%BChlke-engineering-gmbh', 'Thu, 08 Oct 2015 08:02:02 Z', '2015-10-08T08:02:02Z', 669);
