INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (687, 'JavaScript Entwickler fr AR und VR Apps at ViewAR GmbH (Wien, Austria)', '&lt;p&gt;ViewAR ist eines der weltweit f&amp;uuml;hrenden Unternehmen in der Erstellung mobiler Applikationen zur Produktvisualisierung mit Augmented und Virtual Reality Technologien.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Das ViewAR Visualisierungssystem wurde von unserem Team eigens gebaut und wird laufend weiterentwickelt. Unser ViewAR Visualisierungssystem kombiniert eine C++ 3D Rendering Engine f&amp;uuml;r mobile Ger&amp;auml;te mit einem &amp;uuml;berlagerten HTML User Interface. Das HTML User Interface basiert auf AngularJS und ist ein modulares Sytem, das f&amp;uuml;r Kunden individuell angepasst wird.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Unsere Apps sind als Instore-Shopl&amp;ouml;sungen, Vertriebstools sowie Endkundenl&amp;ouml;sungen im Einsatz. Zu unseren Kunden z&amp;auml;hlen mehrere namhafte internationale Unternehmen wie Bang&amp;amp;Olufsen, Lowe&amp;rsquo;s, Intel, Steinway&amp;amp;Sons, Fama, Sky, Kare, Butlers&amp;nbsp;etc.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Hier ein paar Videos unserer Apps:&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;a href="http://bit.ly/1EATo1M" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow"&gt;http://bit.ly/1EATo1M&lt;/a&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;a href="http://bit.ly/1EAThmO" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow"&gt;http://bit.ly/1EAThmO&lt;/a&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Aktuell arbeiten wir unter anderem an der Integration&amp;nbsp;von&amp;nbsp;Intels Tiefenbild Kameras sowie an verschiedensten ViewAR Apps im M&amp;ouml;bel, Architektur und Eventbereich.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Bei der Position soll das System laufend durch neue Funktionalit&amp;auml;ten verbessert werden, sowie dem derzeitigen Entwicklungsteam laufend Support gegeben werden. Gute Englischkenntnisse in Wort und Schrift sind notwendig.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Wir sind ein junges, dynamisches Team aus 10 Personen und bieten hohe Entwicklungs-, Weiterbildungs- und Gestaltungsm&amp;ouml;glichkeiten. Hohe Motivation und Lernf&amp;auml;higkeit setzen wir f&amp;uuml;r die ausgeschriebenen Positionen voraus.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Bei Interesse schicken Sie bitte Ihre aussagekr&amp;auml;ftige Bewerbung inkl. Referenzprojekte an:&lt;/p&gt;&lt;br /&gt;&lt;p&gt;jobs@viewar.com zhd. Hr. Dipl.-Ing. Markus Meixner.&lt;/p&gt;', 'Wien, Austria', 'http://careers.stackoverflow.com/jobs/97246/javascript-entwickler-f%C3%BCr-ar-und-vr-apps-viewar-gmbh', 'Thu, 08 Oct 2015 07:02:02 Z', '2015-10-08T07:02:02Z', 687);
