INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (694, 'Senior .Net Developer / Technical Consultant at UXC Eclipse (Bellevue, WA)', '&lt;p&gt;&lt;br&gt;&lt;strong&gt;Career Opportunity....&amp;nbsp;&lt;br&gt;&lt;/strong&gt;We have an opportunity for an Senior .Net Developer / Technical Consultant to join our Applied Engineering Solutions team.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Working in a team of dynamic and creative professionals, you will be responsible for delivering development services for project based and ad hoc work. This is an excellent time to join a growing global business that looks after its employees and is a great opportunity to apply your skills to high profile, customer facing projects.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;What we offer...&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Fantastic opportunity to extend your skills by working on a variety of exciting projects in a number of different industries&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Emphasis on work life balance.&amp;nbsp; Flexible working conditions and four weeks paid vacation for all new hires!&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Competitive remuneration and benefits package.&amp;nbsp; Twelve weeks paid maternity leave after 18 months of service&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Excellent perks.&amp;nbsp; Free breakfast and lunch provided daily!&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Opportunity to engage with an experienced and supportive team&lt;/li&gt;&lt;br /&gt;&lt;li&gt;401k matching (up to 50% of 6% contribution)&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Choice of Medical coverage and flexible spending accounts.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Life/ADD and Short Term Disability insurance is provided at no cost to you!&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;UXC Eclipse Culture...&lt;/strong&gt;&amp;nbsp;&lt;br&gt;Apart from being a leading business solutions provider to a global market UXC Eclipse is dedicated to &amp;lsquo;putting its people first&amp;rsquo;. We offer competitive remuneration; benefits, training and career opportunities and a culture that demonstrates our employees are our first priority.&lt;/p&gt;', 'Bellevue, WA', 'http://careers.stackoverflow.com/jobs/81318/senior-net-developer-technical-consultant-uxc-eclipse', 'Thu, 08 Oct 2015 05:02:02 Z', '2015-10-08T05:02:02Z', 694);
