INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (700, 'Incident Response Specialist at Rally Health, Inc. (Washington, DC)', '&lt;p&gt;Rally Health seeks an Incident Response Specialist with strong technical skills, and sound written and verbal communication skills.&amp;nbsp; Specialist will maintain the Alert Logic platform(s) and provide ongoing breach detection, incident response ,and administrative services for Infosec Tools and applications. &amp;nbsp; Specialist will also provide regular written status reports and detailed investigation reports for all incidents.&lt;/p&gt;&lt;br /&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Responsibilities:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Maintain constant monitoring of intrusion detection systems&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Create technically detailed reports based on intrusions and events&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Provide assistance in incident investigations&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Coordinate with other teams to remediate detected incidents&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Analyze and evaluate anomalous network and system activity&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Recommend modifications to security tools to detect, prevent, and mitigate intrusions&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Recommend mitigation activities and provide after action reports to remediate vulnerabilities and reduce the chance of further exploitation&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Administer Security related tools and systems that have elevated Access&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Assist in general administration of InfoSec tools and applications&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Perform live response data collection and analysis on hosts of interest in an investigation&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Collate and analyze relevant events from host and network device log files&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Qualifications:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Experience analyzing reports generated from SIEM tools&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Understanding of packet capture (PCAP) analysis and network flow data review&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Experience with Security tools like Alert Logic, TippingPoint IPS, Sophos, Sourcefire IDS/IPS, and/or Fireeye, Splunk&lt;/li&gt;&lt;br /&gt;&lt;li&gt;One or more years&amp;rsquo; experience in a hands-on technical role of network forensics analyst, malware analyst, or incident responder&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Additional Qualifications:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;HS degree required, Higher degree strongly desired.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;One or more of the following technical certifications: GIAC Certified Incident Handler (GCIH), GIAC Certified Forensic Analyst (GCFA), GIAC Reverse Engineering Malware (GREM), EnCE, or equivalent certifications in these areas&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Expertise in analysis of TCP/IP network communication protocols&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Experience with a scripting language such as Perl, Python, or other scripting language in an incident handling environment&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Experience with malware analysis and reverse engineering&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Experience conducting analysis of electronic media, packet capture, log data, and network devices in support of intrusion analysis or enterprise level information security operations&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Washington, DC', 'http://careers.stackoverflow.com/jobs/99853/incident-response-specialist-rally-health-inc', 'Thu, 08 Oct 2015 03:02:02 Z', '2015-10-08T03:02:02Z', 697);
