INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (706, 'Drupal Developer - work with a top travel marketing team! at Miles (Superior, CO) (allows remote)', '&lt;p&gt;As a Drupal Developer with Miles you will be working on some of the largest, most significant websites in the travel industry. Your sites will reach millions of visitors. The development decisions you make will impact the way users plan vacations.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Miles is looking for a well-rounded Drupal Developer with equal experience in front-end and back-end work who is interested in contributing to pushing ideas forward in the tourism space.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Responsibilities&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Development and maintenance of our clients&amp;rsquo; Drupal-based web sites&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Developing and contributing to project plans including estimates, scopes, approaches, schedules and expectations&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Working with other team members to resolve technical issues and problem solving&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Communicating with technical and none technical team members&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Superior, CO', 'http://careers.stackoverflow.com/jobs/90385/drupal-developer-work-with-a-top-travel-miles', 'Thu, 08 Oct 2015 02:02:02 Z', '2015-10-08T02:02:02Z', 706);
