INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (709, 'Front-End Developer - React.js at Redman Technologies Inc (Edmonton, AB, Canada) (allows remote)', '&lt;p&gt;&lt;strong&gt;Who We''re Looking For&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Are you a Front-End Developer who loves to combine the art of programming with the art of design? Do you create great code that is well organized and well structured? Do you have a passion to create a great user experience by visualizing each step the user will take and breaking them into usable components? We are looking for you!.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;As a Front-End Developer at Redman Technologies, you will work within an agile team to bridge the gap between design and technical implementation, creating web components and interfaces. You will have an active role defining how the application will look, feel, and be built.&lt;/p&gt;', 'Edmonton, AB, Canada', 'http://careers.stackoverflow.com/jobs/62312/front-end-developer-reactjs-redman-technologies-inc', 'Thu, 08 Oct 2015 00:02:02 Z', '2015-10-08T00:02:02Z', 709);
