INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (715, 'Software Engineer at Tenable Network Security (Columbia, MD)', '&lt;p&gt;Tenable is looking for a Software Engineer to join the Passive Vulnerability Scanner (PVS) product development team. The Passive Vulnerability Scanner is the only vulnerability scanning product that delivers continuous real-time network monitoring in a non-intrusive manner. As a Software Engineer, you will design, develop and maintain high-speed packet processing and other features for the PVS product.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Can you do this consistently?&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Design, develop and maintain new features in primarily a Linux environment.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Passionate about developing and improving high-speed network applications.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Collaborate with other engineers within an Agile development environment.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Perform code reviews, unit and integration testing to assure a high quality of development efforts.&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Columbia, MD', 'http://careers.stackoverflow.com/jobs/71600/software-engineer-tenable-network-security', 'Thu, 08 Oct 2015 00:02:02 Z', '2015-10-08T00:02:02Z', 712);
