INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (722, 'Sr. Development Engineer at AudienceScience (Pune, India)', '&lt;p&gt;&amp;nbsp;AudienceScience is seeking a talented Sr. Development Engineer to join our team. You&amp;rsquo;ll be responsible for the development of products and tools for the AudienceScience&amp;reg; Helios, a global media buying platform using real-time bidding and behavioral targeting technologies. We currently handle upwards of 9 billion requests per day, aggregating all of this data on our Hadoop clusters. Distributed systems experience a big plus.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&amp;nbsp;RESPONSIBILITIES:&lt;/p&gt;&lt;br /&gt;&lt;p&gt;* Work with Chief Architect to design and build scalable, efficient, distributed systems to meet business objectives following best practices for quality, testability and operability.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;* Perform code reviews.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;* Provide third-tier support for production systems.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;* Work with development leadership and product management to evaluate new product and feature requests and recommend solutions.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;* Provide mentorship to other developers.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;* Stay current on state of the art technologies, and make recommendations for how these may be used to improve our systems.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;* Play key role in recruiting and evaluating candidates for open positions.&lt;/p&gt;', 'Pune, India', 'http://careers.stackoverflow.com/jobs/91462/sr-development-engineer-audiencescience', 'Thu, 08 Oct 2015 00:02:02 Z', '2015-10-08T00:02:02Z', 722);
