INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (740, '.NET Developer at Cignium (Santiago de Surco, Peru)', '&lt;p&gt;We&amp;rsquo;re looking for a creative, talented .NET developer to participate in creating business-to-consumer applications and frameworks supporting millions of transactions of some of the largest companies around.&amp;nbsp; Our systems have hundreds of thousands of business users and we provide large, scalable, enterprise-grade capabilities.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;What You&amp;rsquo;ll Be Doing&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Developing web-based applications using C#, HTML/CSS, JavaScript/JQuery and ASP.Net MVC&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Utilizing your experience with test-driven development (TDD) and Domain Driven Design (DDD)&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Working in a team environment with support for client management, integration testing, requirements analysis, and production support so you can focus on writing great code&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Continually remodeling and refactoring our applications to grow and evolve with current technology, the market, and end-user needs&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Santiago de Surco, Peru', 'http://careers.stackoverflow.com/jobs/82646/net-developer-cignium', 'Wed, 07 Oct 2015 23:02:02 Z', '2015-10-07T23:02:02Z', 740);
