INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (742, 'QA Automation Engineer at Cignium (Santiago de Surco, Peru)', '&lt;p&gt;We are looking for&lt;strong&gt; QA&lt;/strong&gt;&lt;strong&gt; Automation Engineer&lt;/strong&gt; with the min of 3 years of QA Automation Experience. The successful candidate will have experience in designing, building and executing automated tests against Web Applications and Web Services. This position will test your automation skills; you must be proficient in developing &lt;strong&gt;automation&lt;/strong&gt; solutions from ground up and have advanced knowledge of different &lt;strong&gt;automation&lt;/strong&gt; techniques.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;What You&amp;rsquo;ll Be Doing&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Handling all aspects of developing, executing, and maintaining automated test scripts using automation tools.&amp;nbsp; As part of this role, you&amp;rsquo;ll verify the usability, function, performance and scalability of our applications&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Coordinating with the QA Team Lead, Business Analysts and Developers on different teams to work through application defects and enhancements&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Investigating defects in systems and technology in order to pinpoint the potential cause for failed tests&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Locating, identifying, and logging issues; understanding and relaying their relative significance to others on the team&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Ensuring that new functionality meets requirements of functional specifications on a consistent basis&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Santiago de Surco, Peru', 'http://careers.stackoverflow.com/jobs/82647/qa-automation-engineer-cignium', 'Wed, 07 Oct 2015 23:02:02 Z', '2015-10-07T23:02:02Z', 740);
