INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (743, 'Software Engineer at imo.im (Palo Alto, CA)', '&lt;p&gt;We are currently seeking experienced&amp;nbsp;software engineering generalists to work on our messaging platform. We are growing quickly and are looking for developers who have experience with highly-scalable distributed systems. We have 4 main teams here: Backend, Android, iOS, and Audio/Video.&lt;br&gt;&lt;br&gt;Our ideal candidate has a strong background in data structures, algorithms, design, and coding.&amp;nbsp;This is a great opportunity to be an early employee at a fast growing company.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Requirements:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;BS/MS/PhD in computer science (or the equivalent)&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Strong grasp of data structures and algorithms&lt;/li&gt;&lt;br /&gt;&lt;li&gt;1+ years of experience at a company that went through a significant growth stage&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Extra credit:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Experience building and designing scalable distributed systems&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Ability to design and implement systems in Python&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Extensive experience writing networking code in C/C++&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Thorough understanding of TCP/IP and network programming&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Adept at hacking open source software&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Compensation &amp;amp; Perks:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Competitive salary plus $30K starting bonus and stock options&lt;/li&gt;&lt;br /&gt;&lt;li&gt;$500/month housing stipend for living within a five-mile radius of the office&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Four weeks of paid vacation per year (plus paid holidays and sick days)&lt;/li&gt;&lt;br /&gt;&lt;li&gt;401(k), medical, dental and vision benefits&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Access to a concierge medical group&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Catered lunch or dinner daily and a fully-stocked kitchen&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Data plan reimbursement&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Gym membership at Equinox&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Yearly conference stipend&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Custom imo shoes on each of your imo anniversaries&lt;/li&gt;&lt;br /&gt;&lt;li&gt;imo-sponsored team events and outings&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Palo Alto, CA', 'http://careers.stackoverflow.com/jobs/97120/software-engineer-imoim', 'Wed, 07 Oct 2015 22:02:02 Z', '2015-10-07T22:02:02Z', 743);
