INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (752, 'Security Software Engineer (Req. #37329BR) at NetApp (Sunnyvale, CA)', '&lt;p&gt;As a Security Software Engineer you will work as part of CAG Security team responsible for development, testing and debugging of Security focused features in CAG. Security Software Engineers also engage in triage/analysis of security vulnerabilities as well as ensuring that new features are designed and developed in adherence to security best practices, relevant compliance drivers and security policies.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;This position requires an individual to be creative, team-oriented, technology savvy and driven to produce results.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;The CAG security team is involved in a broad set of security related areas including:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Authentication Capabilities&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Certificate Management&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Cluster Peering Security&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Cryptography (user &amp;amp; kernel space)&lt;/li&gt;&lt;br /&gt;&lt;li&gt;FIPS, Common Criteria&lt;/li&gt;&lt;br /&gt;&lt;li&gt;IPsec/IKE&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Key Management&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Security considerations &amp;amp; capabilities for sDOT (evolving)&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Security domain and management for hybrid cloud&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Trusted Computing&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Vulnerability Analysis and Resolution&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Sunnyvale, CA', 'http://careers.stackoverflow.com/jobs/99806/security-software-engineer-req-sharp37329br-netapp', 'Wed, 07 Oct 2015 22:02:02 Z', '2015-10-07T22:02:02Z', 231);
