INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (756, 'Software Engineer  C#/Web/Cloud/Automation at StarRez Pty Ltd (Melbourne, Australia)', '&lt;p&gt;We are looking for a skilled systems developer with a passion for creating world class solutions leveraging the latest cloud technologies!&lt;/p&gt;&lt;br /&gt;&lt;p&gt;This role will be working within our hosting &amp;amp; development teams within an Agile methodology, and reporting to the Systems Manager. &amp;nbsp;You will join a team of highly skilled developers, systems administrators and systems architects to focus on automation and building solutions in a variety of languages and platforms.&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;You will build solutions to create &amp;amp; manage cloud environments, to automate deployments and to enhance our solutions to perform as efficiently as possible within the cloud.&lt;/p&gt;', 'Melbourne, Australia', 'http://careers.stackoverflow.com/jobs/98665/software-engineer-c-sharp-web-cloud-automation-starrez-pty-ltd', 'Wed, 07 Oct 2015 22:02:02 Z', '2015-10-07T22:02:02Z', 756);
