INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (762, 'Senior Front-End Software Engineer at Teikametrics (Boston, MA)', '&lt;p&gt;Join a team of passionate software engineers in a small, rapidly growing online retail SaaS company, and help us make elegant, usable software that delights customers.&amp;nbsp;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;At Teikametrics, you&amp;rsquo;ll use the latest web technologies and play an active role in designing and developing both new and existing products. You&amp;rsquo;ll be expected to learn new technologies quickly and thrive in a fast-paced environment, working both in a team as well as individually.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;As a Senior Front-End Software Engineer, you will lead all aspects of user-facing features and functionality for several B2B SaaS applications and work with our product manager and existing team of full stack developers to deliver an outstanding experience to our customers.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;You will be expected to contribute immediately to both mature and new products and will have a significant impact on our future road map in a short amount of time.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Who are you?&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;br /&gt;&lt;p&gt;You are a &lt;strong&gt;software engineer:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;&lt;br /&gt;&lt;p&gt;with at least 5-7 years professional coding experience&lt;/p&gt;&lt;br /&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;br /&gt;&lt;p&gt;who creates elegant, testable solutions to complex problems&lt;/p&gt;&lt;br /&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;br /&gt;&lt;p&gt;who has experience designing, coding, building and testing software&lt;/p&gt;&lt;br /&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;br /&gt;&lt;p&gt;who has experience debugging and optimizing code&lt;/p&gt;&lt;br /&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;br /&gt;&lt;p&gt;who has experience with functional programming&lt;/p&gt;&lt;br /&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;br /&gt;&lt;p&gt;who is an expert at merging branches &amp;amp; doing code reviews for others&lt;/p&gt;&lt;br /&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;br /&gt;&lt;p&gt;who understands how to make and communicate design tradeoffs&lt;/p&gt;&lt;br /&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;br /&gt;&lt;p&gt;who ideally understands data structures, design patterns and algorithm design&lt;/p&gt;&lt;br /&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;br /&gt;&lt;p&gt;who ideally has used agile development practices like Scrum&lt;/p&gt;&lt;br /&gt;&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;p&gt;You are a &lt;strong&gt;web developer&lt;/strong&gt;:&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;&lt;br /&gt;&lt;p&gt;who builds single page web applications&lt;/p&gt;&lt;br /&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;br /&gt;&lt;p&gt;who is an advanced JavaScript developer&lt;/p&gt;&lt;br /&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;br /&gt;&lt;p&gt;who ideally has worked with one of React, Backbone, Angular or Ember&lt;/p&gt;&lt;br /&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;br /&gt;&lt;p&gt;who ideally has experience developing reusable UI components&lt;/p&gt;&lt;br /&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;br /&gt;&lt;p&gt;who ideally has experience with jQuery&lt;/p&gt;&lt;br /&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;br /&gt;&lt;p&gt;who ideally has started learning ES6 / ES2015&lt;/p&gt;&lt;br /&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;br /&gt;&lt;p&gt;who ideally has some experience with Node.js, Meteor or Ruby on Rails&lt;/p&gt;&lt;br /&gt;&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;p&gt;You are a &lt;strong&gt;UX designer&lt;/strong&gt;:&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;&lt;br /&gt;&lt;p&gt;who has experience in web usability&lt;/p&gt;&lt;br /&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;br /&gt;&lt;p&gt;who is an expert in HTML and CSS&lt;/p&gt;&lt;br /&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;br /&gt;&lt;p&gt;who ideally has good intuition on what makes a delightful user experience&lt;/p&gt;&lt;br /&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;br /&gt;&lt;p&gt;who ideally has experience converting mockups from designers into HTML&lt;/p&gt;&lt;br /&gt;&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;p&gt;You are a &lt;strong&gt;team member&lt;/strong&gt;:&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;&lt;br /&gt;&lt;p&gt;who wants to work with smart, motivated people&lt;/p&gt;&lt;br /&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;br /&gt;&lt;p&gt;who is self-motivated, introspective and driven&lt;/p&gt;&lt;br /&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;br /&gt;&lt;p&gt;who plays nicely with others&lt;/p&gt;&lt;br /&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;br /&gt;&lt;p&gt;who has excellent analytical and problem solving skills&lt;/p&gt;&lt;br /&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;br /&gt;&lt;p&gt;who has excellent written and verbal communication skills&lt;/p&gt;&lt;br /&gt;&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Boston, MA', 'http://careers.stackoverflow.com/jobs/97239/senior-front-end-software-engineer-teikametrics', 'Wed, 07 Oct 2015 22:02:02 Z', '2015-10-07T22:02:02Z', 762);
