INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (764, 'Software Developer at Computrition Inc. (Bedford, MA)', '&lt;p&gt;&lt;strong&gt;Software&amp;nbsp;Developer&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Computrition Inc.&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Boston, MA&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Are you interested in working for a small, agile development shop that has the security of selling software in the healthcare vertical market for 35 years and has the backing of a publicly-traded software conglomerate that has a multi-billion dollar market value?&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Computrition, Inc was founded in 1981 and has primarily been selling enterprise software solutions to the Food and Nutrition departments of Acute Care Hospitals.&amp;nbsp; Our software is also used by the US Marine Corp, most Veteran&amp;rsquo;s Hospitals, as well as other parts of the DoD.&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Working on the R &amp;amp; D team at Computrition is fun, fast-paced, and always interesting.&amp;nbsp; We are a small development team which runs Agile methodology and uses Team Foundation Server on a daily basis.&amp;nbsp; We have a legacy application that is installed in over 1,000 hospitals in North America and we also have introduced applications written in new technologies.&amp;nbsp; With this in mind, we need a seasoned developer who can come in and help us immediately; some stories might require C++ while other stories might be written in C#.&amp;nbsp; All our applications are enterprise level and build on top of an Oracle database.&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Our ideal candidate would be someone who can immediately impact the team by helping to translate business requirements into robust software solutions.&amp;nbsp; They should also be willing to investigate and resolve any known software defects.&amp;nbsp; AND, then can move easily from making feature changes to the main application and next be asked to write something for a brand new add-on module.&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;If you are looking for the newest start-up that is not us!&amp;nbsp; But if you are interested in working for a fun, small development shop that sells software to the healthcare industry, then we are right for you.&amp;nbsp; If you want the feel of a small company but with the security of knowing that we are part of a larger conglomerate, then we might be what you are looking for.&amp;nbsp; AND, if you would rather commute to the technology corridor northwest of Boston than into the city, we are certainly the place you should check out.&amp;nbsp; (&lt;a href="http://www.computrition.com" rel="nofollow" rel="nofollow" rel="nofollow"&gt;www.computrition.com&lt;/a&gt;)&lt;/strong&gt;&lt;/p&gt;', 'Bedford, MA', 'http://careers.stackoverflow.com/jobs/92054/software-developer-computrition-inc', 'Wed, 07 Oct 2015 21:02:02 Z', '2015-10-07T21:02:02Z', 764);
