INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (771, 'Senior Android Engineer - Sports Apps at Omnigon Communications (Santa Monica, CA) (allows remote)', '&lt;p&gt;Would you like the opportunity to work on mobile app projects seen by millions of people daily? Are you interested in developing products for the Sports and Entertainment world? Join us! We are an independent software development shop that creates custom apps and sites for sports, media and entertainment companies. Our team really enjoys working on this stuff.We are looking for highly talented Senior Android Engineers to join our Santa Monica office (we also offer remote work option as well, so out of state candidates are encouraged to apply) .&lt;br&gt;&lt;br&gt;If you work with Android Studio, frameworks Lollipop and moving to Marshmallow, we''d like to hear from you. All engineers write code about 80% of the time and attend meetings and review requirements the other 20%. You will be working on a high profile sports app with users in the 100''s of millions.&lt;/p&gt;', 'Santa Monica, CA', 'http://careers.stackoverflow.com/jobs/99838/senior-android-engineer-sports-apps-omnigon-communications', 'Wed, 07 Oct 2015 21:02:02 Z', '2015-10-07T21:02:02Z', 771);
