INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (778, 'Full stack web developer at CodeWave (Wrocaw, Poland)', '&lt;p&gt;&lt;strong&gt;How we work?&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;a title="Career at CodeWave" href="http://codewave.eu/pl/career.html"&gt;Check the career section of our webstie!&lt;/a&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;CodeWave is a team of friendly people&amp;nbsp;sharing an attitude towards technology and exciting problems. Or exciting technology for solving old&amp;nbsp;problems. Either way - we &lt;a title="flying elephpant" href="https://www.facebook.com/codewave.eu"&gt;make our work fun&lt;/a&gt;, and try to &lt;a title="CodeWave github" href="https://github.com/cdwv/"&gt;give back&lt;/a&gt;.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;We''re growing at a fast pace and we''re looking for skilled web developers that will join a team we''re assembling for a new, exciting project for an international client.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;You''ll join the team at an early stage and so you will take part in the system design, technology selection and spec building processes and finally will work as one of 3-5 developers&amp;nbsp;in the team.&amp;nbsp;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;We&amp;nbsp;don''t make a big fuss about work hours, dress code, etc. We give you the best tools and challenges&amp;nbsp;to build something cool - wether you do it at the office or at home doesn''t matter.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;How we play?&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;We love our work, but we love our free time too. And&amp;nbsp;at those rare occasions when we don''t want to relax hacking -&amp;nbsp;we enjoy BBQs, bowling, lasertag or anything else that makes us unwind. Bumping into your boss with a go kart at a high speed is pretty unwinding, for instance:-)&lt;/p&gt;', 'Wrocaw, Poland', 'http://careers.stackoverflow.com/jobs/99762/full-stack-web-developer-codewave', 'Wed, 07 Oct 2015 21:02:02 Z', '2015-10-07T21:02:02Z', 778);
