INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (784, 'Software Engineer (Charleston and Chicago) at Verge Solutions, LLC (Mount Pleasant, SC)', '&lt;p&gt;&lt;strong&gt;JOB SUMMARY:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;The Software Engineer position at Verge Solutions fulfills a critical role in the development of best of class SaaS software used in support of the health care industry.&amp;nbsp; Essential duties include the design, development and testing of code, primarily within the Microsoft technology stack, on new and legacy platforms.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;This position requires excellent object-oriented programming and design pattern skills while working within an agile environment and a self-managed, cross functional team.&amp;nbsp; The ability to communicate effectively with members of the Product Management, Quality Assurance and Account Management teams is a key component for success in this position.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;JOB RESPONSIBILITIES:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Strategy and Planning&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Forecasting architecture needs and participating in architecture design planning&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Anticipates emerging technology issues that have the potential to affect Verge&amp;rsquo;s customer base&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Provides strategic and technical advice on an enterprise-wide scale&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;p&gt;Analysis, Design and Development&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Collaborate with Product Management and Account Management team members in the analysis of business requirements&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Performs highly complex systems process analysis, design, and simulation as prioritized by Verge&amp;rsquo;s Product Management team&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Designs highly complex application interfaces and integration across diverse systems&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Design and develop high quality, high performing web-based applications&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Executes complete life cycle for complex application components based on functional requirements; considers computer equipment capacity and limitations, operating time and form of desired results&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Able to balance security / compliance needs against business requirements and development speed&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;p&gt;Performance and Capacity Planning&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Collaborate in capacity planning, workload modeling, and prediction to ensure Verge&amp;rsquo;s SaaS solution has sufficient resources for near-term performance and long-term growth&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Enables compliance with service level agreements based on goals, objectives, business partner needs and industry benchmarks&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Recognizes subtle changes or problems in application or system performance, and intervenes flexibly/creatively to improve performance&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;p&gt;Quality Assurance&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Perform unit tests and provide QA Lead with necessary information to create test plans&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Close collaboration with QA team to ensure timely resolution of application defects identified via UAT, Regression testing, etc.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Resolves complex problems, often collaborating with other technical experts to do so&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Adheres to Verge&amp;rsquo;s incident management and reporting process&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;p&gt;Engineering process and SDLC&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Collaborates as needed in the Release and Sprint planning processes to assist in the definition of project scope, objectives, and client requirements, realistic work estimates, financial budgets and project schedules&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Maintaining accurate, timely updates of progress for Scrum Master&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Collaboration with the Software Development Manager to identify process improvement opportunities&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Mentoring software developers and providing expert guidance for solving technical problems and the achievement of quality goals&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Measure progress toward goals and revise work plan accordingly&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Proactively apprises management of issues effecting project status and offer solutions to solve the issues&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Creates a participative, collaborative work environment&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Mount Pleasant, SC', 'http://careers.stackoverflow.com/jobs/99835/software-engineer-charleston-and-chicago-verge-solutions-llc', 'Wed, 07 Oct 2015 21:02:02 Z', '2015-10-07T21:02:02Z', 784);
