INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (787, 'Android Engineer at VTS (New York, NY)', '&lt;p&gt;https://twitter.com/BuildingVTS&lt;/p&gt;&lt;br /&gt;&lt;p&gt;http://building.vts.com/&lt;/p&gt;&lt;br /&gt;&lt;p&gt;https://www.vts.com/&lt;/p&gt;&lt;br /&gt;&lt;p&gt;As a VTS engineer you&amp;rsquo;ll be working on all ends of the product. We&amp;rsquo;re always trying new technology to fit the appropriate situation, and at VTS you''ll definitely pick up a few skills you haven&amp;rsquo;t learned elsewhere.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;We are looking for talented engineers who:&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Are Proficient in Java and OO design practices&lt;br&gt;Can easily navigate Android Studio and the Gradle Build System&lt;br&gt;Demonstrate passion in elegant UI&amp;rsquo;s and animation&lt;br&gt;Have app(s) in the Google Play Store&lt;br&gt;Can demonstrate experience with Agile workflows&lt;br&gt;Are open to Full Stack work using Rails&lt;br&gt;Thrive in a fast-paced, agile startup work environment&lt;/p&gt;&lt;br /&gt;&lt;p&gt;In addition to our standard company benefits, all engineers are provided with their choice of MacBook Pro and keyboard, mouse, and display. Engineers are free to choose their development tools, whether it&amp;rsquo;s vim, Sublime Text, or RubyMine.&lt;/p&gt;', 'New York, NY', 'http://careers.stackoverflow.com/jobs/94606/android-engineer-vts', 'Wed, 07 Oct 2015 21:02:02 Z', '2015-10-07T21:02:02Z', 787);
