INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (797, 'Front End UI Developer - AngularJS at IMLeagues.com (Raleigh, NC) (allows remote)', '&lt;p&gt;The Front End UI Developer will command with authority over a newly installed AngularJS framework and personally build the most complex UI components and set the standard for the user experience while delivering features frequently on an aggressive schedule.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Dramatically improve the quality of the end user experience&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Take over architecture of recently installed AngularJS framework&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Translations of visual designs and mockups to interactive front-end are pixel perfect&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Cross-browser, cross-device compatibility achieved quickly and with minimal rework&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Interactions and animations are world-class, fluid, highly detail oriented, and delightful to use&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Responsive design with multiple breakpoints for most popular tablets and smartphones&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Personally build the most complex UI components with the most elegant solutions&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Features are working end to end very quickly based on the complexity of the requirements&lt;/li&gt;&lt;br /&gt;&lt;li&gt;No awkward pixel left unturned&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Designers are inspired to bring their best to the table, knowing what the team is capable of bringing to life&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Features are configurable at customer/user role/individual end user level&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Raleigh, NC', 'http://careers.stackoverflow.com/jobs/99824/front-end-ui-developer-angularjs-imleaguescom', 'Wed, 07 Oct 2015 20:02:02 Z', '2015-10-07T20:02:02Z', 797);
