INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (799, 'Software Engineer II - Full-Stack Ruby at CareerBuilder (Chicago, IL)', '&lt;p&gt;Our team is building new tools to enable collaboration and quickly manipulate content and applications. In the past we have built API clients, miniature applications, concept vehicles, REST APIs and foundational new libraries. We parachute the field to create software that provides tremendous value.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;We are looking for coders that can break into established areas and innovate using weapons of individual and collective choosing. Our current arsenal is Ruby-heavy and comprised of tools such as Sinatra, Rails, Grape, RiotJS, AngularJS, SASS, ElasticSearch, and GitHub, but current members originate from disparate backgrounds; Perl, Python, .NET, et al.&lt;/p&gt;', 'Chicago, IL', 'http://careers.stackoverflow.com/jobs/99828/software-engineer-ii-full-stack-ruby-careerbuilder', 'Wed, 07 Oct 2015 20:02:02 Z', '2015-10-07T20:02:02Z', 799);
