INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (819, 'Full Stack Developer at Koverse, Inc. (Seattle, WA) (allows remote)', '&lt;p&gt;Solutions Engineer&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Koverse is looking for a dynamic, self-motivated Full Stack Developer&amp;nbsp;that will be part of a world-class&amp;nbsp;team&lt;/p&gt;&lt;br /&gt;&lt;p&gt;working to help customers realize the promise of big data.&amp;nbsp; Based in Seattle and with offices in Washington,DC and Denver, Koverse provides the only demand-driven platform for big data that, from day one, enables&amp;nbsp;users to easily run advanced analytics against any data source and develop result driven applications.&amp;nbsp;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;As a&amp;nbsp;Solutions Engineer, you will work directly with customers during the post-sales process to help ensure&amp;nbsp;smooth adoption of the Koverse platform.&amp;nbsp; This includes hands-on support for setup, developing an&amp;nbsp;implementing custom applications, working with Support to understand and troubleshoot issues, anddelivering customer training.&amp;nbsp;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;This position is ideally located in Seattle, WA; and telecommuting is an option for the right candidate.&amp;nbsp; This&amp;nbsp;position requires frequent travel to customer sites both domestically and internationally.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;As a Solutions Engineer you will:&lt;/p&gt;&lt;br /&gt;&lt;p&gt; Participate in deployment and implementation planning with customers&lt;/p&gt;&lt;br /&gt;&lt;p&gt; Directly assist with installation and setup of the Koverse platform&lt;/p&gt;&lt;br /&gt;&lt;p&gt; Spec, build, test, and deploy customized applications based on customer requirements&lt;/p&gt;&lt;br /&gt;&lt;p&gt; Produce customer documentation to support custom applications&lt;/p&gt;&lt;br /&gt;&lt;p&gt; Contribute technical content and tutorials to product documentation&lt;/p&gt;&lt;br /&gt;&lt;p&gt; Deliver customer training&lt;/p&gt;&lt;br /&gt;&lt;p&gt; Partner with Support to troubleshoot issues&lt;/p&gt;&lt;br /&gt;&lt;p&gt; Partner with the Sales team to qualify potential customers from a technical standpoint&lt;/p&gt;&lt;br /&gt;&lt;p&gt; Develop vertical products on top of the Koverse platform&lt;/p&gt;', 'Seattle, WA', 'http://careers.stackoverflow.com/jobs/99817/full-stack-developer-koverse-inc', 'Wed, 07 Oct 2015 19:02:02 Z', '2015-10-07T19:02:02Z', 819);
