INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (821, 'Senior Software Developer at Voloridge Investment Mgmt (Jupiter, FL)', '&lt;p&gt;The software we develop at Voloridge is a critical part of the company&amp;rsquo;s research and trading operations and contributes directly to the success of the company. Our projects include implementing novel and cutting edge machine learning algorithms which we use with massive quantities of data to model financial markets, simulating financial markets and trading strategies, processing and analyzing real-time market data, and automating futures and equities trading.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;We are seeking a senior level software developer to join our talented development team.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Summary of job functions&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Develop software, primarily in C#&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Significantly contribute to software design and architecture&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Develop both new applications and improve our existing ones in performance, capability, and reliability&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Jupiter, FL', 'http://careers.stackoverflow.com/jobs/99816/senior-software-developer-voloridge-investment-mgmt', 'Wed, 07 Oct 2015 19:02:02 Z', '2015-10-07T19:02:02Z', 821);
