INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (827, 'Senior Database Administrator at Guidepoint (New York, NY)', '&lt;p&gt;Guidepoint gives professionals more confidence in their investment and strategic decisions by connecting them to specialized industry experts who can answer their most pressing business questions. As the experts at finding expertise, since 2003 Guidepoint has set up more than 500,000 interactions between business professionals and experts around the world.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Our multinational client list includes seven of the top 10 global consulting firms, seven of the top 10 largest hedge funds, and many of the largest private equity firms and Fortune-ranked companies.&amp;nbsp;Guidepoint&amp;rsquo;s seven offices on three continents provide 24/7, quick and agile service. For more information, visit &lt;a href="http://www.guidepointglobal.com/" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow"&gt;www.guidepointglobal.com&lt;/a&gt;.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Guidepoint is seeking a Senior Database Administrator with heavy Microsoft SQL Server 2008+ experience to join its engineering team. Our engineering team is a highly collaborative organization consisting of PHP, JavaScript &amp;amp; .NET engineers with the highest levels of technical depth, programming skill and a passion for quality.&lt;/p&gt;', 'New York, NY', 'http://careers.stackoverflow.com/jobs/99814/senior-database-administrator-guidepoint', 'Wed, 07 Oct 2015 19:02:02 Z', '2015-10-07T19:02:02Z', 827);
