INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (842, 'iOS developer (Full Stack) at Express Checkout ltd. (London, UK)', '&lt;p&gt;&lt;strong&gt;&lt;em&gt;Role:&lt;/em&gt;&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Are you interested in solving huge problems that have an impact on everybody''s day?...... i thought so.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Welcome to Express Checkout who are finally reinventing in-store checkout so that customers everywhere will no longer need to queue&amp;nbsp;and wait for a till to buy products. The Express Checkout app allows customers to scan &amp;amp; pay for products in a store simply by using their smartphone.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;We are looking for awesome iOS developers to join our team and help us re-invent shopping for the 21st century. At the moment we are developing our MVP and are soon to be launching our app with select retail partners for our beta launch.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;em&gt;&lt;strong&gt;Role:&lt;/strong&gt;&lt;/em&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;We are looking for an iOS full stack developer to join our product development team.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;This position will create&amp;nbsp;and execute product development plans and sprints.&amp;nbsp;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;The role will tackle difficult problems ranging from designing user experience and handling multiple application integrations.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;The developer&amp;nbsp;will report directly into the CEO&amp;nbsp;of the business.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;This role will have a large degree of autonomy to decide what problems to tackle and prioritise.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;This team member will join the senior management of the company and be a valued owner of the business.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;&lt;em&gt;Opportunity:&lt;/em&gt;&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Joining our team will mean you get:&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;to be involved in high level strategic planning for the business&lt;/li&gt;&lt;br /&gt;&lt;li&gt;to design and execute your own start up experiments&lt;/li&gt;&lt;br /&gt;&lt;li&gt;invent your own features and have a huge say in what we build, iterate and test&lt;/li&gt;&lt;br /&gt;&lt;li&gt;work with a tight knit team that is supportive, open and prepared to try anything once&lt;/li&gt;&lt;br /&gt;&lt;li&gt;sit in the cockpit and get the best seat for the ride&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'London, UK', 'http://careers.stackoverflow.com/jobs/97233/ios-developer-full-stack-express-checkout-ltd', 'Wed, 07 Oct 2015 17:02:02 Z', '2015-10-07T17:02:02Z', 842);
