INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (850, 'Senior Python Developer at DrugDev Ltd (London, UK)', '&lt;p&gt;As a member of the Data Solutions division of DrugDev, the Senior Developer will provide a leading role in the backend development of unique industry tools. The role provides an opportunity to join a rapidly-growing team that is bringing together technology-enabled solutions to drive standards in the clinical trial industry and transforming the way that clinical trials are run.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Job Summary&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;We are seeking a&amp;nbsp;Senior Python Developer whose primary focus will be to:&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Become an&amp;nbsp;expert in all palettes of python products in our application portfolio&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Work closely with the Principal Solution Architect, and collaborate strongly with other members of the development team&lt;/li&gt;&lt;br /&gt;&lt;li&gt;To grow with the organisation as the company continues its&amp;nbsp;rapid growth&amp;nbsp;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;To showcase your backend knowledge with opportunities to develop innovative technology solutions&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'London, UK', 'http://careers.stackoverflow.com/jobs/99801/senior-python-developer-drugdev-ltd', 'Wed, 07 Oct 2015 17:02:02 Z', '2015-10-07T17:02:02Z', 850);
