INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (854, 'Sr PHP Engineer - Full Stack at Gozengo (New York, NY)', '&lt;p&gt;We&amp;rsquo;re looking for a Senior PHP Engineer &amp;ndash; Full Stack to help us define and build our products &lt;em&gt;and&lt;/em&gt; since it&amp;rsquo;s a start-up, won&amp;rsquo;t mind doing anything else we need. You&amp;rsquo;ll be working collaboratively to architect, design and build innovative solutions for our users.&lt;/p&gt;&lt;br /&gt;&lt;br /&gt;&lt;p&gt;What you need to know:&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;We will be building in LAMP, using Symfony2 framework.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;You will focus on the back-end initially, but eventually get to traverse the stack if you so desire.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;You will be challenged on a daily basis as we will be solving some very complex problems involving large data sets and multiple external APIs.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;We only hire the best talent.&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'New York, NY', 'http://careers.stackoverflow.com/jobs/99800/sr-php-engineer-full-stack-gozengo', 'Wed, 07 Oct 2015 17:02:02 Z', '2015-10-07T17:02:02Z', 854);
