INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (858, 'iOS iPad Developer at Videri, Inc. (New York, NY)', '&lt;p&gt;Position Responsibilities:&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Design, develop, and maintain cutting edge new application technology using agile and TDD methodology.&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Analyze and troubleshoot code, correct errors as required.&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Develop unit, regression, and integration testing.&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Work with the product management team to help define new product features.&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Provide engineering support to operations as required.&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Follow industry standard applications development techniques and practices.&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Create accompanying documentation to software code.&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Participate in development meetings and provide status reports.&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'New York, NY', 'http://careers.stackoverflow.com/jobs/82882/ios-ipad-developer-videri-inc', 'Wed, 07 Oct 2015 17:02:02 Z', '2015-10-07T17:02:02Z', 856);
