INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (867, 'Software Developer Java (m/w) at serie a logistics solutions AG (Kln, Deutschland)', 'serie a logistics solutions AG &amp;ndash; Weinsbergstr. 190 &amp;ndash; 50825 K&amp;ouml;ln&lt;br&gt;&lt;br&gt;Kontakt&lt;br&gt;&lt;br&gt;Deine aussagekr&amp;auml;ftige Bewerbung mit fr&amp;uuml;hestm&amp;ouml;glichen Eintritt sowie deinen Gehaltsvorstellungen sendest Du bitte an bewerbung@serie-a.de.&lt;br&gt;&lt;br&gt;Deine Bewerbung&lt;br&gt;&lt;br&gt;- Jobticket f&amp;uuml;r K&amp;ouml;ln und Umland&lt;br&gt;- Betriebliche Altersvorsorge&lt;br&gt;- Schnelle Einarbeitung durch unser Mentoring-Programm&lt;br&gt;- Leistungsstarke Arbeitsplatz-Hardware&lt;br&gt;- Freie Wahl von OS (Linux, MacOS, Windows)&lt;br&gt;- Einsatz von Open Source Technologien&lt;br&gt;- Flache Hierarchien&lt;br&gt;- Ein dynamisches und hochmotiviertes Team aus professionellen Entwicklern, DB- und System Operatoren, Qualit&amp;auml;ts- und Produktmanagern&lt;br&gt;&lt;br&gt;Neben einer attraktiven und leistungsgerechten Verg&amp;uuml;tung bieten wir ein angenehmes Arbeitsklima und die M&amp;ouml;glichkeit zur kontinuierlichen Weiterbildung, die wir durch regelm&amp;auml;&amp;szlig;ige Fortbildungen f&amp;ouml;rdern.&lt;br&gt;&lt;br&gt;Wir bieten&lt;br&gt;&lt;br&gt;- Du beherrschst die deutsche und englische Sprache sehr gut in Wort und Schrift&lt;br&gt;- Du verf&amp;uuml;gst &amp;uuml;ber ein abgeschlossenes Studium in Informatik oder gleichwertige in der Praxis erworbene Kenntnisse&lt;br&gt;- Deine Kenntnisse der objektorientierten Softwareentwicklung in Java werden erg&amp;auml;nzt durch fundierte Kenntnisse in Web- und Datenbankprogrammierung&lt;br&gt;- Du verf&amp;uuml;gst &amp;uuml;ber Projekterfahrung im Bereich Software-Engineering, u.a. mit dem Springframework aber auch &amp;auml;hnlichen Frameworks&lt;br&gt;- Begeisterung f&amp;uuml;r das Internet und dessen Basistechnologien&lt;br&gt;&lt;br&gt;Du bist eine dynamische und innovative Pers&amp;ouml;nlichkeit mit technischem Verstand, Flexibilit&amp;auml;t und Sozialkompetenz. Loyalit&amp;auml;t und hohe eigene Qualit&amp;auml;tsanspr&amp;uuml;che sichern Nachhaltigkeit und Kontinuit&amp;auml;t. Im Java-Umfeld bist Du ein Profi oder willst einer werden, aber auch andere Technologien sind in deinem Fokus. Folgende Eigenschaften treffen dabei auf Dich zu:&lt;br&gt;&lt;br&gt;Dein Profil&lt;br&gt;&lt;br&gt;- Durch Dein solides Wissen &amp;uuml;ber HTTP, SQL, WebServices und Linux sowie dem sicheren Umgang mit Datenbanken &amp;uuml;bernimmst Du schnell Verantwortung&lt;br&gt;- Du konzipierst und realisierst komplexe fachliche L&amp;ouml;sungen auf Basis von &amp;bdquo;State-of-the-Art&amp;ldquo;-Technologien wie z.B. Springframework, HTML-5, Camunda und WebServices, JSON, REST&lt;br&gt;- Als Teil eines Teams &amp;uuml;bernimmst Du Verantwortung f&amp;uuml;r ein System, ein Softwaremodul oder ein Projekt - (Web, Frontend und Backend)&lt;br&gt;- Agile Entwicklung in kurzen Zyklen&lt;br&gt;&lt;br&gt;Das erwartet Dich&lt;br&gt;&lt;br&gt;- Neue Wege suchen und gehen &amp;ndash; mit den Teams!&lt;br&gt;- Dokumentation der technischen Spezifikationen und der entwickelten Applikationen&lt;br&gt;- Einbindung in den kompletten Software-Lebenszyklus&lt;br&gt;- Projektleitung / Teilprojektleitung&lt;br&gt;- Effiziente Umsetzung von kundenspezifischen Anforderungen&lt;br&gt;- Erarbeitung von L&amp;ouml;sungswegen / -vorschl&amp;auml;gen / Prototypen&lt;br&gt;- Anforderungsanalyse gemeinsam mit dem Kunden&lt;br&gt;&lt;br&gt;Als Software Developer erstellst Du L&amp;ouml;sungskonzepte f&amp;uuml;r moderne, web-basierte Enterprise-Software und implementierst diese in einem jungen und engagierten Team auf Basis von &amp;bdquo;State-of-the-Art&amp;ldquo;-Technologien. Durch Deine Erfahrungen, Ideen und Vorstellungen hilfst Du, unseren agilen Entwicklungsprozess st&amp;auml;ndig zu verbessern.&lt;br&gt;&lt;br&gt;Deine Aufgaben&lt;br&gt;&lt;br&gt;Software Developer Java (m/w)&lt;br&gt;&lt;br&gt;Die serie a logistics solutions AG ist der Software-Partner f&amp;uuml;r alle Themen rund um die Logistik. serie a bietet Komplettl&amp;ouml;sungen f&amp;uuml;r Logistik-Unternehmen und die verladende Industrie aus einer Hand - von der Analyse &amp;uuml;ber die Konzeption bis hin zur erfolgreichen Umsetzung. Zur Verst&amp;auml;rkung unseres Entwicklerteams suchen wir ab sofort:', 'Kln, Deutschland', 'http://careers.stackoverflow.com/jobs/99797/software-developer-java-m-w-serie-a-logistics-solutions-ag', 'Wed, 07 Oct 2015 17:02:02 Z', '2015-10-07T17:02:02Z', 867);
